function run(data) {
    /** @type {DataForScript} */
    const ddd = data
    const nameManager = ddd.project.getPreset('FakerNameMap').propertyManager
    const propertyManager = ddd.project.getPreset('FakerProperty').propertyManager
    const typeManager = ddd.project.getPreset('FakerTypeMap').propertyManager

    /** @type {Map<string, Entity>} */
    const idMap = new Map()
    ddd.entity.relationManager.list.forEach(relation => {
        // User::class
        const textxx = relation.parameter.split('::')
        if (textxx.length) {
            const entity = ddd.project.getEntity(textxx[0])
            if (entity) {
                idMap.set(relation.name + '_id', entity)
            }
        }
    })

    ddd.entity.fieldManager.list.forEach(field => {
        if (field.isIncrement) {
            return
        }
        if (field.seed.value) {
            return
        }

        const entity = idMap.get(field.name)
        if (entity) {
            field.seed.value = entity.name + '::factory()'
            return
        }

        let found = nameManager.find(field.name)
        if (found) {
            /**
             * if `field.name` found in preset `FakerNameMap`
             * then set `field.seed`
             * `found.tag` is one of [method, property, raw]
             * `found.value` is one of [name in FakerMethod, name in FakerProperty, PHP expression]
             */
            field.seed.type = found.tag
            field.seed.value = found.value
            return
        }

        found = typeManager.find(field.type)
        if (found) {
            /**
             * if `field.type` found in preset `FakerTypeMap`
             * then set `field.seed`
             * `found.tag` is one of [method, property, raw]
             * `found.value` is one of [name in FakerMethod, name in FakerProperty, PHP expression]
             */
            field.seed.type = found.tag
            field.seed.value = found.value
            return
        }

        found = propertyManager.find(field.name)
        if (found) {
            /**
             * if `field.name` found in preset `FakerProperty`
             * then set `field.seed`
             */
            field.seed.type = 'property'
            field.seed.value = found.name
        }

    })
}

