function run(data) {
    /** @type {DataForScript} */
    const ddd = data
    const integerManager = ddd.project.getPreset('FieldTypeInteger').propertyManager
    const numericManager = ddd.project.getPreset('FieldTypeNumeric').propertyManager
    const nameManager = ddd.project.getPreset('ValidationNameMap').propertyManager
    const ruleManager = ddd.project.getPreset('ValidationRule').propertyManager

    /** @type {Map<string, Entity>} */
    const idMap = new Map()
    ddd.entity.relationManager.list.forEach(relation => {
        // User::class
        const textxx = relation.parameter.split('::')
        if (textxx.length) {
            const name = textxx[0]
            const entity = ddd.project.getEntity(name)
            if (entity) {
                idMap.set(name.toLocaleLowerCase() + '_id', entity)
            }
        }
    })

    const nameSet = new Set()
    ruleManager.list.forEach(rule => {
        if (rule.tag === 'WWW') {
            // rule.name like [email, ip, url, ...]
            nameSet.add(rule.name)
        }
    })

    /**
     * add rule to a field
     * @param {Field} field
     * @param {string} name
     * @returns {Rule}
     */
    function addRule(field, name) {
        const found = field.ruleManager.find(name)
        if (found) {
            return found
        }
        const rule = field.ruleManager.make(name)
        field.ruleManager.add(rule)
        return rule
    }

    ddd.entity.fieldManager.list.forEach(field => {
        if (field.included === false) {
            return
        }
        if (field.isIncrement) {
            return
        }

        addRule(field, 'required')

        const entity = idMap.get(field.name)
        if (entity) {
            const rule = addRule(field, 'exists')
            rule.value = `${entity.tableName},id`
        }

        const found = nameManager.find(field.name)
        if (found) {
            /**
             * if `field.name` found in preset `ValidationNameMap`
             * then add rule `found.tag`
             */
            const rule = addRule(field, found.tag)
            if (rule.value === '') {
                rule.value = found.value
            }
        }

        if (field.type === 'boolean') {
            addRule(field, 'boolean')
            return
        }
        if (integerManager.find(field.type)) {
            addRule(field, 'integer')
            return
        }
        if (numericManager.find(field.type)) {
            addRule(field, 'numeric')
            return
        }
        if (field.type === 'char' || field.type === 'string') {
            if (field.length) {
                const max = addRule(field, 'max')
                max.value = field.length
            }
        }

        if (nameSet.has(field.name)) {
            // field.name like [email, ip, url, ...]
            addRule(field, field.name)
        }
    })
}

