function run(data) {
    /** @type {DataForScript} */
    const ddd = data
    ddd.model = ddd.project.getLayer('app/Models', 'Model')

    /** @type {Set<Entity>} */
    const entitySet = new Set([ddd.entity])
    ddd.entity.relationManager.list.forEach(relation => {
        // User::class
        const textxx = relation.parameter.split('::')
        if (textxx.length) {
            const entity = ddd.project.getEntity(textxx[0])
            if (entity) {
                entitySet.add(entity)
            }
        }
    })

    /**
     * set seed text
     * @param {Seed} seed
     * @returns {string}
     */
    function setSeed(seed) {
        const list = ['$this->faker']
        if (seed.unique) {
            list.push('unique()')
        }

        if (seed.type === 'property') {
            list.push(seed.value)
            return list.join('->')
        }

        if (seed.type === 'method') {
            list.push(`${seed.value}(${seed.parameter})`)
            return list.join('->')
        }

        return seed.value
    }

    ddd.seedText = setSeed

    ddd.entityxx = Array.from(entitySet.values())
        .sort((aa, bb) => aa.name.localeCompare(bb.name))
}

function run(data) {
    /** @type {DataForScript} */
    const ddd = data

    /**
     * make PHP statement of creating field in Migration
     * @param {Field} field
     * @returns {string}
     */
    function setText(field) {
        const list = ['$table']

        if (field.length) {
            list.push(`${field.type}('${field.name}', ${field.length})`)
        } else {
            list.push(`${field.type}('${field.name}')`)
        }

        if (field.collation) {
            list.push(`collation(${field.collation})`)
        }

        if (field.comment) {
            list.push(`comment(${field.comment})`)
        }

        if (field.value) {
            list.push(`default(${field.value})`)
        }

        if (field.allowNull) {
            list.push('nullable()')
        }

        if (field.useCurrent) {
            list.push('useCurrent()')
        }

        if (field.unsigned) {
            list.push('unsigned()')
        }

        return list.join('->')
    }

    ddd.fieldText = setText

    ddd.indexText = function (index) {
        const text = index.fieldManager.list.map(field => field.name).join("', '")
        return `$table->${index.type}(['${text}'])`
    }
}

