function run(data) {
    /** @type {DataForScript} */
    const ddd = data

    const layerController = ddd.project.getLayer('app/Http/Controllers', 'ControllerAPI')
    ddd.nsxx = []
    ddd.groupxx = []
    ddd.project.entityManager.list.forEach(entity => {
        const routexx = []
        const controller = layerController.getClassName(entity)
        // make route list of entity
        entity.routeManager.list.forEach(route => {
            let routeName = ''
            if (route.routeName) {
                routeName = `->name('${route.routeName}')`
            }

            let middleware = ''
            if (route.middlewareManager.list.length) {
                const list = route.middlewareManager.list.map(mw => mw.name)
                middleware = `->middleware(['${list.join("', '")}'])`
            }

            const text = `Route::${route.method}('${route.path}', [${controller}::class, '${route.name}'])${routeName + middleware};`
            routexx.push(text)
        })

        // if the entity has route, then make a group
        if (routexx.length) {
            const ns = layerController.getFullName(ddd.project, entity)
            ddd.nsxx.push(ns)

            const list = []
            if (entity.routeDomain) {
                list.push(`domain('${entity.routeDomain}')->`)
            }

            if (entity.routeName) {
                list.push(`name('${entity.routeName}')->`)
            }

            let prefix = ddd.project.apiVersion
            if (entity.routePrefix) {
                prefix = prefix + '/' + entity.routePrefix
            }
            if (prefix) {
                list.push(`prefix('${prefix}')->`)
            }

            const namexx = entity.middlewareManager.list.map(mw => mw.name)
            const mw = namexx.length ? `'${namexx.join("', '")}'` : ''
            list.push(`middleware([${mw}])->`)

            const text = `Route::${list.join('')}group(function () {\n    ${routexx.join('\n    ')}\n});`
            ddd.groupxx.push(text)
        }
    })
}

